import constructorStyles from './BurgerConstructor.module.css'
import { ConstructorElement } from '@ya.praktikum/react-developer-burger-ui-components'
import React from 'react'
function BurgerConstructor({ element }) {
    const buns = React.useMemo(() => element.find((m) => m.type === "bun"), [element])
    return (
        <section className={constructorStyles.container}>
            <div className={`${constructorStyles.content}`}>
                <ul className={constructorStyles.list}>
                    <li><ConstructorElement
                        type="top"
                        isLocked={true}
                        text={`${buns.name} (верх)`}
                        price={buns.price}
                        thumbnail={buns.image}
                    /></li>
                    {element.map((item) => {
                        return item.type !== "bun" && (
                            <li>
                                <ConstructorElement
                                className={`${constructorStyles.ddd} custom-scroll`}
                                    text={item.name}
                                    price={item.price}
                                    thumbnail={item.image}
                                /></li>
                        )
                    })}
                    <li><ConstructorElement
                        type="bottom"
                        isLocked={true}
                        text={`${buns.name} (низ)`}
                        price={buns.price}
                        thumbnail={buns.image}
                    /></li>
                </ul>
            </div>
        </section>
    )
}

export default BurgerConstructor
