import constructorStyles from './BurgerConstructor.module.css'
import { ConstructorElement, DragIcon } from '@ya.praktikum/react-developer-burger-ui-components'
import React from 'react'
function BurgerConstructor({ element }) {
    const buns = React.useMemo(() => element.find((m) => m.type === "bun"), [element])
    return (
        <section className={`${constructorStyles.container} ml-5 ml-5`}>
            <div className={`${constructorStyles.content} pl-4 pr-4 pb-5 pt-5`}>
                <ConstructorElement
                    type="top"
                    isLocked={true}
                    text={`${buns.name} (верх)`}
                    price={buns.price}
                    thumbnail={buns.image}
                />
                <ul className={`${constructorStyles.list}  custom-scroll`}>
                    {element.map((item) => {
                        return item.type !== "bun" && (
                            <li>
                                <DragIcon type="primary" />
                                <ConstructorElement
                                    text={item.name}
                                    price={item.price}
                                    thumbnail={item.image}
                                /></li>
                        )
                    })}
                </ul>
                <ConstructorElement
                    type="bottom"
                    isLocked={true}
                    text={`${buns.name} (низ)`}
                    price={buns.price}
                    thumbnail={buns.image}
                />
            </div>
        </section>
    )
}

export default BurgerConstructor
