import ingredientsStyle from './BurgerIngredients.module.css'
import { Tab, CurrencyIcon, Counter } from '@ya.praktikum/react-developer-burger-ui-components'
import React from 'react'
import {data} from '../../utils/data.js'
import { func } from 'prop-types'
import IngredientsList from '../IngredientsList/IngredientsList'


function BurgerIngredients() {
    const [current, setCurrent] = React.useState('one')
    const bun = "bun"
    const buns = React.useMemo(
        () => {
            data.filter(m => {
                m.type === bun,
                [data]
            })
        }
    )
    return (
        <main className={ingredientsStyle.main}>
            <div className={ingredientsStyle.content}>
                <div className={ingredientsStyle.ingredientsContainer}>
                    <h2 className={`${ingredientsStyle.title} text text_type_main-large`}>Соберите бургер</h2>
                    <div className={ingredientsStyle.tab}>
                        <Tab value="one" active={current === 'one'} onClick={setCurrent}>
                            One
                        </Tab>
                        <Tab value="two" active={current === 'two'} onClick={setCurrent}>
                            Two
                        </Tab>
                        <Tab value="three" active={current === 'three'} onClick={setCurrent}>
                            Three
                        </Tab>
                    </div>
                    <h3 className={`${ingredientsStyle.subtitle} text text_type_main-default`}>Булки</h3>
                    <ul className={ingredientsStyle.list}>
                        {buns.map((data) => {
                            <li key={data._id}>
                                <IngredientsList data={data} />
                            </li>
                        })}
                    </ul>
                </div>
            </div>
        </main>
    )
}

function kek() {
    return
}
export default BurgerIngredients
