import constructorStyles from './BurgerConstructor.module.css'
import { ConstructorElement } from '@ya.praktikum/react-developer-burger-ui-components'
import React from 'react'
function BurgerConstructor({ element }) {
    const buns = React.useMemo(() => element.find((m) => m.type === "bun"), [element]) 
    return (
        <section className={constructorStyles.container}>
            <div className={`${constructorStyles.content} custom-scroll`}>
                <ul className={constructorStyles.list}>
                    <li><ConstructorElement
                        type="top"
                        isLocked={true}
                        text={`${buns.name} (верх)`}
                        price={buns.price}
                    /></li>
                    <li><ConstructorElement
                        type="bottom"
                        isLocked={true}
                        text="Краторная булка N-200i (низ)"
                        price={200}
                    /></li>
                </ul>
            </div>
        </section>
    )
}

export default BurgerConstructor
